{"filter":false,"title":"sessions_controller.rb","tooltip":"/app/controllers/sessions_controller.rb","undoManager":{"mark":1,"position":1,"stack":[[{"start":{"row":21,"column":5},"end":{"row":22,"column":0},"action":"insert","lines":["",""],"id":5},{"start":{"row":22,"column":0},"end":{"row":22,"column":2},"action":"insert","lines":["  "]}],[{"start":{"row":22,"column":2},"end":{"row":79,"column":3},"action":"insert","lines":["# Logs in the given user.","  def log_in(user)","    session[:user_id] = user.id","  end","","  # Remembers a user in a persistent session.","  def remember(user)","    user.remember","    cookies.permanent.signed[:user_id] = user.id","    cookies.permanent[:remember_token] = user.remember_token","  end","  # Returns true if the given user is the current user.","  def current_user?(user)","    user == current_user","  end","  "," # Returns the current logged-in user (if any).","  def current_user","    if (user_id = session[:user_id])","      @current_user ||= User.find_by(id: user_id)","    elsif (user_id = cookies.signed[:user_id])","      user = User.find_by(id: user_id)","      if user && user.authenticated?(:remember, cookies[:remember_token])","        log_in user","        @current_user = user","      end","    end","  end","","  # Returns true if the user is logged in, false otherwise.","  def logged_in?","    !current_user.nil?","  end",""," # Forgets a persistent session.","  def forget(user)","    user.forget","    cookies.delete(:user_id)","    cookies.delete(:remember_token)","  end","","  # Logs out the current user.","  def log_out","    forget(current_user)","    session.delete(:user_id)","    @current_user = nil","  end","  # Redirects to stored location (or to the default).","  def redirect_back_or(default)","    redirect_to(session[:forwarding_url] || default)","    session.delete(:forwarding_url)","  end","","  # Stores the URL trying to be accessed.","  def store_location","    session[:forwarding_url] = request.original_url if request.get?","  end","end"],"id":6}]]},"ace":{"folds":[],"scrolltop":845.4545593261719,"scrollleft":0,"selection":{"start":{"row":79,"column":3},"end":{"row":79,"column":3},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":60,"state":"start","mode":"ace/mode/ruby"}},"timestamp":1493678873112,"hash":"a34e7f335051bf28b6e84b969acea2cc9f07f3fd"}